import React, { Component } from 'react'
import {SafeAreaView,Text,StyleSheet,FlatList } from 'react-native'
import Styles from './styles'
import cursos from './src/cursos'
import { render } from 'react-dom'
import api from './src/Services/api'
import Curso from './src/Pages/Curso'

class CursosList extends Component {

  constructor(props){
    super(props);
    this.state={
      cursos:[]
    }
  }

  async componentDidMount(){
    const response = await api.get('pessoasassistidas/?page=2&page_size=1');
    this.setState({
      cursos:response.data
    });
  }

  render() {
    return (
 <SafeAreaView>
  <FlatList 
  data={this.state.cursos}
  keyExtractor={item=>item.id}
  renderItem={({item})=><Curso data={item}/>}
  />

 </SafeAreaView>
    )
  }
}

export default CursosList
