import React, {Component} from 'react';
import { View ,Text,Image,StyleSheet ,TouchableOpacity} from 'react-native';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button } from 'reactstrap';
import { Card } from 'reactstrap';

export default class Curso extends Component{
    render(){


        return(

            <View>
                <Card>
                    <Text style={styles.nomeCurso}>ID Assistido:   {this.props.data.id}</Text>
                    <TouchableOpacity onPress={()=> alert(this.props.data.nome)}>
                    <Text style={styles.nomeCurso}>Nome Assistido: {this.props.data.nome}</Text>
                    </TouchableOpacity>
                    <Text style={styles.nomeCurso}>Email Assistido: {this.props.data.email}</Text>
                    <Text style={styles.nomeCurso}>Email Naturalidade: {this.props.data.naturalidade}</Text>
                    <Text style={styles.nomeCurso}>Email Naturalidade: {this.props.data.enderecos[0].logradouro}</Text>
                    <Button color="success">Salvar</Button>
                    <Button color="danger">Excluir</Button>
                </Card>

            </View>

        )


    }
}

const styles = StyleSheet.create({
    container:{

    },
    nomeCurso:{
        color:'white',
        fontSize:18,
        fontWeight:'bold',
        justifyContent:'center',
        alignSelf:'Center',
        marginTop:30,
        marginBottom:20
    }
  })
